CREATE VIEW backup_statistics AS
SELECT 
    COUNT(*) as total_backups,
    COUNT(CASE WHEN status = 'completed' THEN 1 END) as successful_backups,
    COUNT(CASE WHEN status = 'failed' THEN 1 END) as failed_backups,
    COUNT(CASE WHEN is_auto_backup = 1 THEN 1 END) as auto_backups,
    COUNT(CASE WHEN is_auto_backup = 0 THEN 1 END) as manual_backups,
    SUM(file_size) as total_size_bytes,
    AVG(file_size) as avg_size_bytes,
    MAX(created_at) as last_backup_date,
    COUNT(CASE WHEN DATE(created_at) = DATE('now') THEN 1 END) as today_backups
FROM backup_metadata;
